'use strict';

var util = {};

util.getParams = function (url, key) {
    url = url.slice(url.indexOf('?') + 1);

    var list = url.split('&');
    var params = {};

    list.map(function (item) {
        var tmp = item.split('=');
        params[tmp[0]] = tmp[1];
    });

    return key ? params[key] : params;
};

util.urlSetParams = function (url, data) {
    var anchor = '';

    if (url.indexOf('#') > -1) {
        anchor = url.slice(url.indexOf('#'));
        url = url.slice(0, url.indexOf('#'));
    }

    if (url.indexOf('?') > -1) {
        var path = url.slice(0, url.indexOf('?'));

        // 获取目标url的参数
        var params = util.getParams(url);

        // 删掉同名参数
        // let hasParams = false;
        Object.keys(params).map(function (key) {
            // hasParams = true;

            Object.keys(data).map(function (k) {
                if (key.toLowerCase() == k.toLowerCase()) {
                    delete params[key];
                }
            });
        });

        // 添加data
        Object.keys(data).map(function (k) {
            params[k] = data[k];
        });

        // if (hasParams) {
        var _paramsList = [];

        Object.keys(params).map(function (key) {
            _paramsList.push(key + '=' + params[key]);
        });

        return path + '?' + _paramsList.join('&') + anchor;
    }
    var paramsList = [];

    Object.keys(data).map(function (k) {
        paramsList.push(k + '=' + data[k]);
    });
    return url + '?' + paramsList.join('&') + anchor;
};

module.exports = util;