import expect from 'expect.js';
import Enzyme, { mount } from 'enzyme';
import Adapter from 'enzyme-adapter-react-16';
import React from 'react';
import Preview from '../src/Preview';
import File from './mocks/File';

Enzyme.configure({ adapter: new Adapter() });

describe('Preview', () => {
  it('should render correctly for images', (done) => {
    const src = 'data:image/svg+xml;base64,';
    const file = new File();
    file.url = src;
    file.type = 'image/svg+xml';
    file.ext = 'svg';
    const wrapper = mount(<Preview file={file} />);
    setTimeout(() => {
      const img = wrapper.update().find('img');
      expect(img.is({ src })).to.be(true);
      done();
    }, 100);
  });

  it('should render correctly for non-images', () => {
    const file = new File();
    file.ext = 'json';
    file.type = 'application/json';
    const wrapper = mount(<Preview file={file} prefixCls={'kuma-upload'} />);
    const i = wrapper.find('i');
    expect(i.is({
      className: 'kuma-upload-fileicon',
      'data-ext': file.ext,
      'data-type': file.type,
    })).to.be(true);
  });
});
